export function loadLists() {
  const vetor = []
  const titles = ['Tarefas', 'Fazendo', 'Pausado', 'Concluído']

  const cards = {
    title: '', 
    creatable: true,
    cards: [
      {
        id: null,
        content: '',
        labels: [],
        user: ''
      },
    ]
  }

  titles.forEach(card => {
      const json = localStorage.getItem(card)
      vetor.push(JSON.parse(json))
  })

  const list = vetor
  const listArray = [JSON.stringify(list)]
  const listArrayJson = JSON.parse(listArray)
  return listArrayJson;
}
