    const [color, setColor] = useState('');

    const cards = { 
        title: 'Fazendo',
        creatable: true,
        cards: [
          {
            id: 1,
            content: 'Frase',
            labels: ['#7159c1'],
            user: 'https://rocketseat-cdn.s3-sa-east-1.amazonaws.com/profile.png'
          }
        ]
    }

    const card = [JSON.stringify(cards)]
